    this.setState({
      isOpen: false
    })
  }
  //设置一个方法来修改 state 的状态值
  render(){
    return(


        //将状态值绑定给插件的 isOpen 属性

          //将改变状态值的方法绑定在你需要触发的节点上
            关闭



    )
  }
}

export default Sidebar

