    }
    shouldComponentUpdate(nextProps,nextState){
        console.log('test should')
        return true
    }
    componentWillUpdate(){
        console.log('test Update')
    }
    componentDidUpdate(){
        console.log('test did Update')
    }
    render(){
        console.log('text render')
        return (

                {this.props.num}

        )
    }
}
export default Test
